
global N
N = 5

def eulers_8th(S,n,best_seq):
    if n < (1000 - N):
        seq = list(S[n:n+N])
        for i in range(len(seq)): seq[i] = int(seq[i])
        if seq[N-1] == 0:
            print("jump! form:", n, "to:",n+N)
            n = n + N
        elif sum(best_seq) < sum(seq):
            best_seq = []
            for i in seq: best_seq.append(i)
            print("found new best! ", best_seq)
        print(n, " sum:",sum(seq), (0 in seq), "current best: ", sum(best_seq))
        return eulers_8th(S,n+1,best_seq)
    else:
        return best_seq

